using System.Collections.Generic; namespace gLibraries { public class gCookie { public static IDictionary<string, object> all() { IDictionary<string, object> lista = Xamarin.Forms.Application.Current.Properties; return lista; } public static object get(string name) { try { return all()[name]; } catch { return null; } } public static bool set(string name, object value) { Xamarin.Forms.Application.Current.Properties[name] = value; return true; } public static bool clean() { List<string> keys = keys = (List<string>)all().Keys; keys.ForEach(key => { Xamarin.Forms.Application.Current.Properties.Remove(key); }); return true; } public static bool clean(string key) { Xamarin.Forms.Application.Current.Properties.Remove(key); return true; } public static bool clean(List<string> keys) { keys.ForEach(key => { Xamarin.Forms.Application.Current.Properties.Remove(key); }); return true; } } }